/*Table structure for table `circle` */

DROP TABLE IF EXISTS `circle`;

CREATE TABLE `circle` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT '圈子及评论',
  `vid` varchar(20) DEFAULT NULL COMMENT '圈子ID',
  `circle_type` tinyint(1) DEFAULT '1' COMMENT '圈子类型，1普通，2带店铺，3带名片',
  `about_id` int(11) DEFAULT '0' COMMENT '当circle_type=1时为0；\r\n当circle_type=2时为店铺ID；\r\n当circle_type=3时为名片id；',
  `content` varchar(255) DEFAULT NULL COMMENT '评论和回复的限制字数长度为200字',
  `imgs` text COMMENT '上传图片最多9张(回复不能上传图片)',
  `parent_id` int(11) DEFAULT '0',
  `level` tinyint(3) DEFAULT '1' COMMENT '层级',
  `type` tinyint(1) DEFAULT NULL COMMENT '用户类型 1企业厂商，2业务员',
  `fu_id` int(11) DEFAULT NULL COMMENT 'type=1:关联厂商表;\r\ntype=2:关联业务员表',
  `comments` int(11) DEFAULT '0' COMMENT '评论数',
  `collection` int(11) DEFAULT '0' COMMENT '收藏数',
  `area` varchar(60) DEFAULT '' COMMENT '发布城市',
  `create_time` datetime DEFAULT NULL,
  `update_time` datetime DEFAULT NULL COMMENT '更新时间',
  `is_delete` tinyint(1) DEFAULT '0' COMMENT '是否删除 0否，1是',
  PRIMARY KEY (`id`)
