/*Table structure for table `firms` */

DROP TABLE IF EXISTS `firms`;

CREATE TABLE `firms` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT '厂商表',
  `vid` int(11) DEFAULT '9' COMMENT '序号',
  `uname` varchar(20) DEFAULT '' COMMENT '昵称',
  `EnterpriseID` varchar(10) NOT NULL DEFAULT '' COMMENT '企业ID(唯一)',
  `phone` varchar(16) DEFAULT '' COMMENT '手机号(企业账号)',
  `password` varchar(255) DEFAULT '' COMMENT '企业登录密码(默认7777777)',
  `type` tinyint(1) DEFAULT NULL COMMENT '1.经销商  2.修理厂',
  `classification` tinyint(1) DEFAULT NULL COMMENT '企业分类 (和type字段相关)\r\n经销商：1.轿车商家 2.货车商家 3.物流货运\r\n汽修厂：4.修理厂    5.快修保养 6.美容店',
  `business` varchar(255) DEFAULT '' COMMENT '经营范围(当type=1，经销商使用)\r\n和经销商及车系表car_group关联\r\n1个经销商自己最多可以选择3个二级分类经营范围，但后台可配置多个，前端进行动态布局\r\n车系是指同一个经销商下是有4级分类的，只是在用户界面只提供到2级的筛选，该2级就是经营范围，最多可以选3个，但经营范围下的3、4级就不限数量',
  `is_showfactry` tinyint(1) DEFAULT '2' COMMENT '汽修厂轨迹权限  1有 2无',
  `scale` varchar(4) DEFAULT '' COMMENT '企业规模(大，中，小)(当type=2，修理厂使用)',
  `companyname` varchar(60) DEFAULT '' COMMENT '企业名称',
  `province` varchar(20) DEFAULT '' COMMENT '省',
  `city` varchar(20) DEFAULT '' COMMENT '区',
  `district` varchar(20) DEFAULT '' COMMENT '区',
  `address` varchar(255) DEFAULT '' COMMENT '详细地址',
  `coordinate` varchar(64) DEFAULT '' COMMENT '坐标名称',
  `longitude` decimal(12,8) DEFAULT NULL COMMENT '经度',
  `latitude` decimal(12,8) DEFAULT NULL COMMENT '纬度',
  `face_pic` varchar(255) DEFAULT '' COMMENT '封面  建议200*200px',
  `major` varchar(255) DEFAULT '' COMMENT '主营',
  `linkMan` varchar(30) DEFAULT '' COMMENT '联系人',
  `linkPhone` varchar(255) DEFAULT '' COMMENT '联系手机号(可填多个)',
  `linkTel` varchar(255) DEFAULT '' COMMENT '座机(可填多个)',
  `qq` varchar(255) DEFAULT '' COMMENT 'QQ号(可填多个)',
  `create_time` datetime DEFAULT NULL,
  `update_time` datetime DEFAULT NULL,
  `last_time` datetime DEFAULT NULL COMMENT '最近一次登陆时间',
  `status` tinyint(1) DEFAULT '2' COMMENT '状态 1.正常 2.禁用',
  `salesman_ids` varchar(255) DEFAULT '' COMMENT '关联业务员id(sales_user表)，例如:  ,1,2,31,',
  `wechat_pic` varchar(255) DEFAULT '' COMMENT '微信二维码',
  `info` text COMMENT '企业介绍',
  `is_vip` tinyint(1) DEFAULT '2' COMMENT '是否vip   1是  2否 (汽修厂没有VIP会员)',
  `is_check` tinyint(1) DEFAULT '2' COMMENT '是否认证  1是 2否',
  `refresh_point` int(11) DEFAULT '0' COMMENT '刷新点',
  `is_sales` tinyint(1) DEFAULT '0' COMMENT '是否是推荐经销商 1是 0否',
  `refresh_time` datetime DEFAULT NULL COMMENT '刷新点时间',
  `invite_code` varchar(30) DEFAULT '' COMMENT '邀请码(唯一)',
  `vip_time` datetime DEFAULT NULL COMMENT 'vip到期时间',
  `factory_grades` tinyint(1) DEFAULT '1' COMMENT '修理厂等级 （1,2,3）',
  `session_id` varchar(26) DEFAULT '' COMMENT 'session_id',
  `head_pic` varchar(255) DEFAULT '' COMMENT '头像图片',
  `four_ids` text COMMENT '四级id('',''分割),（经营范围）',
  `QR_pic` varchar(255) DEFAULT '' COMMENT '店铺二维码地址',
  PRIMARY KEY (`id`),
  UNIQUE KEY `EnterpriseID` (`EnterpriseID`),
  UNIQUE KEY `invite_code` (`invite_code`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=230 DEFAULT CHARSET=utf8 COMMENT='厂商表';

