  `city` varchar(255) DEFAULT '0',
  PRIMARY KEY (`id`),
  KEY `id` (`id`) USING BTREE,
  KEY `toId` (`toId`) USING BTREE,
  KEY `aboutId` (`aboutId`) USING BTREE
) ENGINE=MyISAM AUTO_INCREMENT=327 DEFAULT CHARSET=utf8mb4 COMMENT='消息通知';

