/*Table structure for table `pay_history` */

DROP TABLE IF EXISTS `pay_history`;

CREATE TABLE `pay_history` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT '收费记录表',
  `type` tinyint(1) DEFAULT NULL COMMENT '充值类型  1充值VIP  2充值刷新点 3消费刷新点 4获得新点',
  `status` tinyint(1) DEFAULT NULL COMMENT '状态 1成功 2失败',
  `info` varchar(255) DEFAULT NULL COMMENT '详情',
  `payway` tinyint(1) DEFAULT NULL COMMENT '支付方式 1微信支付  2支付宝支付 3人工收费 4刷新产品 5 刷新店铺 6 邀请获得 7官方赠送',
  `refresh_point` int(11) DEFAULT '0' COMMENT '刷新点数  100或-100',
  `vip_month` int(11) DEFAULT '0' COMMENT '充值vip的月数',
  `refrsh_prduct_id` int(11) unsigned DEFAULT NULL COMMENT '产品id(仅用于刷新产品)',
  `firms_id` int(11) DEFAULT NULL COMMENT '厂商id',
  `money` decimal(10,2) DEFAULT NULL COMMENT '充值金额（元）',
  `admin_id` int(11) DEFAULT NULL COMMENT '管理员id 没有为0',
  `create_time` datetime DEFAULT NULL,
  `order_no` varchar(128) DEFAULT '' COMMENT '微信订单号或支付宝订单号',
  `out_trade_no` varchar(64) DEFAULT '' COMMENT '订单商户号',
  `coder` varchar(20) DEFAULT NULL COMMENT '订单号',
  PRIMARY KEY (`id`),
  KEY `coder` (`coder`) USING BTREE,
  KEY `refrsh_prduct_id` (`refrsh_prduct_id`) USING BTREE
) ENGINE=MyISAM AUTO_INCREMENT=20 DEFAULT CHARSET=utf8 COMMENT='收费记录表';

