/*Table structure for table `sales_wage_log` */

DROP TABLE IF EXISTS `sales_wage_log`;

CREATE TABLE `sales_wage_log` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT '业务员工资记录',
  `sales_user_id` int(11) DEFAULT NULL COMMENT '业务员id',
  `year_month` date DEFAULT NULL COMMENT '年月份',
  `base_wage` decimal(10,2) DEFAULT '0.00' COMMENT '基本工资',
  `subsidies` decimal(10,2) DEFAULT '0.00' COMMENT '补助',
  `new_firm_num` int(11) DEFAULT '0' COMMENT '新增关联厂汽修厂',
  `new_firm_prop` decimal(11,2) DEFAULT '0.00' COMMENT '新增汽修厂关联提成比例',
  `new_firm_comm` decimal(10,2) DEFAULT '0.00' COMMENT '新增关联汽修厂总提成 \r\n明细从firms_sales_user表中获取',
  `firm_num` int(11) DEFAULT '0' COMMENT '关联汽修厂来电数（移动端拨打电话+QQ）',
  `firm_prop` decimal(11,2) DEFAULT '0.00' COMMENT '汽修厂关联提成比例',
  `firm_comm` decimal(10,2) DEFAULT '0.00' COMMENT '汽修厂关联提成，是按照该月来电数（移动端拨打电话+QQ）乘以固定系数算出的',
  `firm_recharge_money` decimal(11,2) DEFAULT '0.00' COMMENT '关联厂商充值总金额',
  `firm_recharge_prop` decimal(11,2) DEFAULT '0.00' COMMENT '充值提成',
  `firm_recharge_comm` decimal(11,2) DEFAULT '0.00' COMMENT '关联厂商充值提成，是按照该月经销商和汽修厂充值金额乘以固定系数算出的，充值包括了买VIP、充值点数和后台人工增加的充值金额（人工开通VIP或增加刷新点，需增加财务数据）',
  `repair_lv1_used` int(11) DEFAULT '0' COMMENT '一级汽修厂使用量',
  `repair_lv1_prop` decimal(11,2) DEFAULT '0.00',
  `repair_lv2_used` int(11) DEFAULT '0' COMMENT '二级汽修厂使用量',
  `repair_lv2_prop` decimal(11,2) DEFAULT '0.00',
  `repair_lv3_used` int(11) DEFAULT '0' COMMENT '三级汽修厂使用量',
  `repair_lv3_prop` decimal(11,2) DEFAULT '0.00',
  `repair_use_money` decimal(10,2) DEFAULT '0.00' COMMENT '汽修厂厂使用频率提成  \r\n针对汽修厂使用频率的提成，是统计关联后30天内，该月达到对应使用等级的关联汽修厂的数量提成，等级同验证厂商中的汽修厂等级定义，如3月1日，关联修理厂满30天的，达到等级1的汽修厂有200个，达到等级2的有60个，达到等级3的有40个，乘以对应的提成单价，已提成的修理厂就不再计入下一个月的提成；\r\n到1号时，没有关联到30天的，这部分汽修厂的提成计入到下一个月的提成',
  `repair_lv1_called` int(11) DEFAULT '0',
  `call_lv1_prop` decimal(11,2) DEFAULT '0.00',
  `repair_lv2_called` int(11) DEFAULT '0',
  `call_lv2_prop` decimal(11,2) DEFAULT '0.00',
  `repair_lv3_called` int(11) DEFAULT '0',
  `call_lv3_prop` decimal(11,2) DEFAULT '0.00',
  `repair_call_money` decimal(10,2) DEFAULT '0.00' COMMENT '针对汽修厂关联提成，是按照该月来电数（移动端拨打电话+QQ）乘以固定系数算出的',
  `total` decimal(10,2) DEFAULT '0.00' COMMENT '合计',
  `is_show` tinyint(1) DEFAULT '0' COMMENT '是否显示给业务员  1是 2否\r\n\r\n每月1号统计上1个月的提成和工资；每个月工资由后台审核/确认后才会更新，未更新统计为“0”',
  `create_time` datetime DEFAULT NULL,
  PRIMARY KEY (`id`)
